mapper.select:
select course_id as courseId, course_name as courseName, remark, type, teach_type as teachType from course where is_del = true
and course_name like '%${courseName}%'
and type = #{type}
and teach_type = #{teachType}

mapper.update:
update course

    course_id = #{courseId} 
    course_name = #{courseName} 
    remark = #{remark} 
    type = #{type} 
    teach_type = #{teachType} 
    update_time = #{updateTime} 
    creator = #{creator} 

where course_id = #{courseId}
