mapper.select:
select c.course_id as courseId, c.course_name as courseName, c.audit_status, t1.teacher_name as checkUser, t2.teacher_name as creator from course left join teacher t1 on t1.teacher_id = c.check_user left join teacher t2 on t2.teacher_id = c.creator where is_del = true
and course_name like concat ('%',#{courseName},'%')
and type = #{type}
and teach_type = #{teachType}

select
    course_id
from course
where course_id = #{courseId}
    and choice_time between #{startTime} and #{endTime}

mapper.update:
update course

    course_id = #{courseId} 
    course_name = #{courseName} 
    remark = #{remark} 
    type = #{type} 
    teach_type = #{teachType} 
    update_time = #{updateTime} 
    creator = #{creator} 

where course_id = #{courseId}
